import './App.css';
// import HomePage from './views/HomePage';
// import AboutPage from './views/AboutPage';
import React, {useEffect, useState} from 'react';
import Recipe from './components/Recipe';
import mongoose from 'mongoose';
require('dotenv/config')

function App() {
  const APP_ID = "85b5b9d5";
  const APP_KEY = "cc2a61eb50ae063e203c421f27e4524d";

  const [recipe, setRecipe] = useState([]);
  const [search, setSearch] = useState('');
  const [query, setQuery] = useState('');

  // DB connection
  mongoose.connect(proces.env.DB_URI, {useNewUrlParser:true, useUnifiedTopology:true})
  .then (() => {
      console.log('DB connected')
  })
  .catch( (e) => {
    console.log(e)
  })

  useEffect( () => {
    getRecipes()
  }, [query]);

    // const randomDish = ['salad', 'chat', 'pav bhaji','biryani', 'samosa', 'noodles'] ;


    const getRecipes = async () => {
      const response = await fetch(`https://api.edamam.com/api/recipes/v2?type=public&q=${query}&app_id=${APP_ID}&app_key=${APP_KEY}`)
      const data =  await response.json('');
      setRecipe(data.hits);
    };


  const updateSearch = (e) => {
      setSearch(e.target.value);
  };

  const getSearch = (e) => {
    e.preventDefault();
    setQuery(search);
    setSearch('');
  };

  // const PORT = process.env.PORT || 4000; //backend routing port

  return (
    <div className="App">
      <form onSubmit={getSearch} className='searchForm'>
        <input className='search-bar' type='text' value={search} onChange={updateSearch} />
        <button className='search-button' type='submit'>Search</button>
      </form>
      {recipe.map(recipe => (
        <Recipe title={recipe.recipe.label}
        healthlables={recipe.recipe.healthLables}
        ingredients={recipe.recipe.ingredients}
        image={recipe.recipe.image}
        />
      ))}
    </div>
  );
}

export default App;
